-- MEMBER에 NICNAME 변경후 트리거 발생(NOTICE, SPONSOR, VREPLY,DBOARD)
CREATE OR REPLACE TRIGGER MEMBER_NICKNAME_UPDATE
AFTER UPDATE OF NICKNAME ON MEMBER FOR EACH ROW
BEGIN
    UPDATE NOTICE
    SET NOTICE_WRITER = :NEW.NICKNAME
    WHERE NOTICE_WRITER = :OLD.NICKNAME;
    UPDATE SPONSOR
    SET S_ID = :NEW.NICKNAME
    WHERE S_ID = :OLD.NICKNAME;
   UPDATE VREPLY 
    SET NICKNAME = :NEW.NICKNAME
    WHERE NICKNAME = :OLD.NICKNAME;
    UPDATE DBOARD
    SET D_WRITER = :NEW.NICKNAME
    WHERE D_WRITER = :OLD.NICKNAME;
    UPDATE DREPLY
    SET DRE_WRITER = :NEW.NICKNAME
    WHERE DRE_WRITER = :OLD.NICKNAME;
END;
/
commit;
